mapper.update:
UPDATE BLOG
SET BOOK_ID = #{book_id}
review_title =
#{book_title}
review_content = #{review_content}
review_rating =
#{review_rating}
WHERE blog_id = #{blog_id}

mapper.select:
SELECT * FROM (
SELECT ROWNUM RN, A.*
FROM (
SELECT * FROM BLOG WHERE MEMBER_ID = #{member_id}
) A
)
WHERE RN BETWEEN #{start_no} AND #{end_no}
