mapper.select:
select * from  member
where member_id = #{member_id}

	and member_password = #{member_password}

mapper.update:
update member
member_password = #{member_password}, member_name = #{member_name}, member_author = #{member_author}, member_tel = #{member_tel}, member_address = #{member_address}, member_picture = #{member_picture}, member_pfile = #{member_pfile}, member_gender = #{member_gender}, member_bir = #{member_bir}, genre_id = #{genre_id},
where member_id = #{member_id}
