mapper.select:
select * from  member
where member_id = #{member_id}

	and member_password = #{member_password}

mapper.update:
update member
set member_password = #{member_password}
set member_name = #{member_name}
set member_author = #{member_author}
set member_tel = #{member_tel}
set member_address = #{member_address}
set member_picture = #{member_picture}
set member_pfile = #{member_pfile}
set member_gender = #{member_gender}
set member_bir = #{member_bir}
set genre_id = #{genre_id}
where member_id = #{member_id}
